	function edb_seo_replace_tokens( $tokens, $matrix ){
	  $nm = array();
	  foreach( $matrix as $name => $value ){
	    foreach($tokens as $k=>$v){
	      if(!empty($v)){
	        write_log("$k: $v");
	        $value = str_replace( $k, $v, $value );   
	      }
      }
      $nm[$name] = $value;
    }
	  
	  return $nm;
	}
	function edb_seo_matrix(){
	  global $seo_matrix;
	  global $product;
	  
	  $lang =WPGlobus::Config()->language;
	  $path = parse_url($_SERVER['REQUEST_URI'], PHP_URL_PATH);
	  
	  $tokens = array('PRODUCT_NAME' => '','PRODUCT_KIND' => '');
	  if(is_product()){
	   $path = str_replace($product, 'PRODUCT_ID', $path );
	   $deco = edb_decorated_product( get_the_ID() );
	   $tokens = array('PRODUCT_NAME'=>ucwords($deco->full_name),'PRODUCT_KIND'=> __($deco->main_category, 'edb') );
	  }
	  
	  
	  
	  
	  
	  if(isset($seo_matrix[$lang][$path])){
	    $matrix = $seo_matrix[$lang][$path];  
	  }
	  
	 
	  if(empty($matrix)){
	 
	    if($lang == 'fr'){
	      $matrix = array(
         'keywords' => '',
         'title'=>'Magasin en ligne de meubles et accessoires à Montréal | Élément de base',
         'description'=> 'Bienvenue sur Élément de base, magasin de meubles et accessoires de décoration à Montréal. Découvrez nos produits et nos échantillons et magasinez en ligne.',
         'heading'=> 'Magasin en ligne de meubles et accessoires à Montréal'
        );
	    }else{
	    $matrix = array(
       'keywords' => '',
       'title'=>'Living room furniture and accessories in Montreal | Élément de base',
       'description'=> 'Welcome to Élément de base, living room furniture and accessories in Montreal. Discover our products, lookbooks and fabric samples and shop online now!',
       'heading'=> 'Living room furniture and accessories in Montreal'
      );  
	    }
	    
	  }
	  
	  $matrix = edb_seo_replace_tokens( $tokens, $matrix);
	  
    return $matrix;
    
	  
	}
	
	function edb_seo_meta_tags(){
	  $matrix = edb_seo_matrix();
	  $title = ucfirst($matrix['title']);
    $keywords = $matrix['keywords'];
    $description = $matrix['description'];
    $heading = ucfirst($matrix['heading']);
    
    echo "<title>$title</title>";
    if(!empty($keywords)){
      echo "<meta name=\"keywords\" content=\"$keywords\" />";  
    }
    if(!empty($description)){
      echo "<meta name=\"description\" content=\"$description\" />";  
    }
	}
	
	function edb_seo_heading( $text ){
	  $matrix = edb_seo_matrix();
	  if($matrix['heading']){
	    echo $matrix['heading'];
	  }else{
	    echo $text;
	  }
	}
	
	function get_facebook_product_meta(){
	  global $post;
	  $matrix = edb_seo_matrix();
	  $deco = edb_decorated_product($post->ID);
	  $meta = array();
	  $meta['og:title'] = $matrix['heading'];
	  $meta['og:url'] = $deco->url;
	  $meta['og:description'] = $matrix['description'];
	  
	  $meta['og:image'] = $deco->images['featured'];
	 // $meta['og:site-name'] = 'élément de base';
	  $meta['og:type'] = 'product';
	  $meta['product:category'] = $deco->main_category;
	  $meta['product:product_link'] = $deco->url;
	  $meta['product:retailer']  = '1773523889541962';
	  $meta['product:brand']  = 'élément de base';
	  return $meta;
	}
	function get_default_facebook_meta(){
	  global $post;
	  $meta = array();
	  $path = $_SERVER['REQUEST_URI'];
	  $uri='https://elementdebase.com'.$path;
	  $meta['og:url'] = rtrim($uri, '/');
	  if(is_product_category()){
	    $meta['og:type'] = 'product.group';
	  }else if(is_page()){
	    $meta['og:type'] = 'article';
	  }else{
	    $meta['og:type'] = 'website';
	  }
	  $meta['og:title'] = 'Living room furniture and accessories in Montreal | Élément de base';
    $meta['og:description'] = 'Élément de base, living room furniture and accessories in Montreal. Discover our products, lookbooks and fabric samples and shop online now!';
    $meta['og:image'] = 'https://elementdebase.com/wp-content/uploads/2016/02/images_sections_0000_contact.jpg';
    return $meta;
	}
	
	
	function facebook_meta_tags(){
	  if(is_product()){
	    $meta = get_facebook_product_meta();
	  }else{
	    $meta = get_default_facebook_meta();
	  }
	  foreach($meta as $prop => $content ){
	    echo '<meta property="'.esc_attr($prop).'" content="'.esc_attr($content).'">';
	  }
	}
	
